mapper.insert:
insert into t_phoneinfo (Id, Phone, Name, 
  CreateTime, Uptimestamp, DataState, 
  IMEI, PhoneType, Des, 
  SeatId, UnitId, Status
  )
values (#{id,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=VARCHAR}, #{uptimestamp,jdbcType=VARCHAR}, #{dataState,jdbcType=VARCHAR}, 
  #{IMEI,jdbcType=VARCHAR}, #{phoneType,jdbcType=VARCHAR}, #{des,jdbcType=VARCHAR}, 
  #{seatId,jdbcType=VARCHAR}, #{unitId,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
  )

mapper.select:
	select
	c.*, s.name seatName, a.name areaName,u.name unitName
	from t_phoneinfo c

left join t_seatinfo s on c.seatId=s.id
left join t_areainfo a on s.areaId=a.id

left join t_unitinfo u on a.unitId=u.id


	where 1=1
		
							and u.id  in (select u1.id from t_unitinfo_manager  u
left join t_unitinfo u1 on  (u.UnitId=u1.id or u.UnitId=u1.parentUnit)
 where u.managerId=#{curUid}
 )
	
	

	
		and c.id = #{id}
	
		
		and c.name  like   concat('%',#{name},'%')
		
	
	
		
		and c.phone  like   concat('%',#{phone},'%')
		
	
	
		
		and c.IMEI  like   concat('%',#{IMEI},'%')
		
	
	
		
		and s.id=  #{seatId}
		
	
	
		
		and a.id=  #{areaId}
		
	
	
		
		and u.id=#{unitId}
