mapper.update:
UPDATE price_lock_company
SET Price = #{price,jdbcType=DECIMAL}
WHERE Id = #{id,jdbcType=BIGINT}

UPDATE price_lock_company
SET Price = #{price,jdbcType=DECIMAL}
WHERE lockCompanyId = #{lockCompanyId,jdbcType=BIGINT}
AND serverType = #{serverType}
AND parentType = #{parentType};

mapper.select:
SELECT t_locksmith_enterprise.`Id` as id,t_locksmith_enterprise.`EnterpriseName` as enterpriseName
,t_locksmith_enterprise.`EnterprisePhone` as enterprisePhone ,price_lock_company.Id as lockCompanyPriceId
,price_lock_company.serverType,price_lock_company.typeName,price_lock_company.Price as price
,price_lock_company.parentType,price_lock_company.createTime,price_lock_company.updateTime
FROM t_locksmith_enterprise
LEFT JOIN price_lock_company
ON t_locksmith_enterprise.`Id`=price_lock_company.`lockCompanyId`
WHERE t_locksmith_enterprise.Id = #{lockCompanyId,jdbcType=BIGINT}
AND price_lock_company.serverType = #{serverType}
AND price_lock_company.parentType = #{parentType}
