mapper.select:
select c.*,a.name areaName, u.name unitName,cnum.num personNumReal from t_seatinfo c left join t_areainfo a on c.areaId=a.id left join t_unitinfo u on a.unitId=u.id left join ( select s.id,count(*) num from t_seatinfo s left join t_receivepoliceinfo p on p.SeatId=s.id where 1=1 and p.id is not null group by s.id) cnum on c.id=cnum.id
where 1=1
and u.id in (select u1.id from t_unitinfo_manager u left join t_unitinfo u1 on (u.UnitId=u1.id or u.UnitId=u1.parentUnit) where u.managerId=#{curUid} )
and c.id = #{id}
and u.id = #{unitId}
and a.id = #{areaId}
and c.Name like concat ('%', #{name},'%')
