mapper.select:
select c.*,o.orderNo,u.name userName,u.Phone userPhone from t_user_back_question c left join t_orderinfo o on o.Id=c.orderInfoId left join t_userinfo u on u.Id=c.userId where 1=1
and c.id = #{id}
and c.userId = #{userId}
and c.orderInfoId = #{orderInfoId}
and u.name like concat ('%', #{userName},'%')
and c.messageContent like concat ('%', #{messageContent},'%')
and c.messageTitle like concat ('%', #{messageTitle},'%')
and c.serviceState=#{serviceState}
order by c.createTime desc
