mapper.select:
select

from manager
where 1=1 and datastate = 1
and (
TELEPHONE = #{key}
or username =
#{key,jdbcType=VARCHAR}
)

select

from manager
where USERNAME=#{name,jdbcType=VARCHAR} and PASSWORD=#{password,jdbcType=VARCHAR}

select group_concat( r.name,'') userRole, nickname,
t1.ID id, USERNAME username, PASSWORD password, TELEPHONE telephone, TELEPHONE_VERIFIED telephone_verified,
TOKEN token,type, TIME time, VERIFY_KEY verify_key, CREATE_DATE create_date,
CREATER creater,HEAD_IMG head_img,icon,companyId
from manager t1
left join sys_user_role ur on t1.ID=ur.sys_user_id
left join sys_role r on r.id=ur.sys_role_id
where 1=1

    and ID= #{id,jdbcType=VARCHAR}


    and USERNAME like concat('%',#{username},'%')


    and PASSWORD = #{password,jdbcType=VARCHAR}


    and TELEPHONE = #{telephone,jdbcType=VARCHAR}


    and TELEPHONE_VERIFIED = #{telephoneVerified,jdbcType=INTEGER}


    and TOKEN = #{token,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and TIME = #{time,jdbcType=VARCHAR}


    and VERIFY_KEY = #{verifyKey,jdbcType=VARCHAR}


    and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    and CREATER = #{creater,jdbcType=VARCHAR}


    and HEAD_IMG = #{headImg,jdbcType=LONGVARBINARY}

group by t1.id

select group_concat( r.name,'') userRole, nickname,
t1.ID id, USERNAME username, PASSWORD password, TELEPHONE telephone, TELEPHONE_VERIFIED telephone_verified,
TOKEN token,type, TIME time, VERIFY_KEY verify_key, CREATE_DATE create_date,DataState dataState,
CREATER creater,HEAD_IMG head_img,icon,companyId
from manager t1
left join sys_user_role ur on t1.ID=ur.sys_user_id
left join sys_role r on r.id=ur.sys_role_id
where t1.type = 2 and t1.DataState != 2

    and ID= #{id,jdbcType=VARCHAR}


    and USERNAME like concat('%',#{username},'%')


    and PASSWORD = #{password,jdbcType=VARCHAR}


    and TELEPHONE = #{telephone,jdbcType=VARCHAR}


    and TELEPHONE_VERIFIED = #{telephoneVerified,jdbcType=INTEGER}


    and TOKEN = #{token,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and TIME = #{time,jdbcType=VARCHAR}


    and VERIFY_KEY = #{verifyKey,jdbcType=VARCHAR}


    and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    and CREATER = #{creater,jdbcType=VARCHAR}


    and HEAD_IMG = #{headImg,jdbcType=LONGVARBINARY}


    and companyId = #{companyId,jdbcType=VARCHAR}

group by t1.id

select
t1.ID id, USERNAME username, PASSWORD password, TELEPHONE telephone, TELEPHONE_VERIFIED telephone_verified,
TOKEN token,type, TIME time, VERIFY_KEY verify_key, CREATE_DATE create_date,
CREATER creater,HEAD_IMG head_img,icon
from manager t1
where 1=1

    and t1.ID= #{id,jdbcType=VARCHAR}


    and USERNAME = #{username,jdbcType=VARCHAR}


    and PASSWORD = #{password,jdbcType=VARCHAR}


    and TELEPHONE = #{telephone,jdbcType=VARCHAR}


    and TELEPHONE_VERIFIED = #{telephoneVerified,jdbcType=INTEGER}


    and TOKEN = #{token,jdbcType=VARCHAR}


    and type = #{token,jdbcType=INTEGER}


    and TIME = #{time,jdbcType=VARCHAR}


    and VERIFY_KEY = #{verifyKey,jdbcType=VARCHAR}


    and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    and CREATER = #{creater,jdbcType=VARCHAR}


    and HEAD_IMG = #{headImg,jdbcType=LONGVARBINARY}

select
count(*)
from manager
where 1=1 and DataState!=2

    and NICKNAME = #{nickname}


    AND id!=#{id }

select group_concat( r.name,'') roleName, nickname,
t1.ID id, USERNAME username, PASSWORD password, TELEPHONE telephone, TELEPHONE_VERIFIED telephone_verified,
TOKEN token,type, TIME time, VERIFY_KEY verify_key, CREATE_DATE create_date,
CREATER creater,HEAD_IMG head_img,icon,t1.CompanyId companyId
from manager t1
left join sys_user_role ur on t1.ID=ur.sys_user_id
left join sys_role r on r.id=ur.sys_role_id
where 1=1 and DataState!=2

    and t1.ID= #{id,jdbcType=VARCHAR}


    and USERNAME = #{username,jdbcType=VARCHAR}


    and PASSWORD = #{password,jdbcType=VARCHAR}


    and TELEPHONE = #{telephone,jdbcType=VARCHAR}


    and TELEPHONE_VERIFIED = #{telephoneVerified,jdbcType=INTEGER}


    and TOKEN = #{token,jdbcType=VARCHAR}


    and type = #{type,jdbcType=INTEGER}


    and TIME = #{time,jdbcType=VARCHAR}


    and VERIFY_KEY = #{verifyKey,jdbcType=VARCHAR}


    and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    and CREATER = #{creater,jdbcType=VARCHAR}


    and HEAD_IMG = #{headImg,jdbcType=LONGVARBINARY}


group by t1.id

mapper.update:
UPDATE manager
SET DataState = 2

WHERE id = #{id,jdbcType=VARCHAR};

UPDATE manager
SET DataState = 0

WHERE id = #{id,jdbcType=VARCHAR};

UPDATE manager
SET DataState = 1

WHERE id = #{id,jdbcType=VARCHAR};
